mapper.select:
select deptlist.* from (select ROW_NUMBER() over (order by d.department_id asc) idx, d.*,(select count(*) from employee where department_id=d.DEPARTMENT_ID) count, e.EMPLOYEE_NAME from DEPARTMENT d,employee e where e.employee_ID(+) = d.manager_id and d.department_id != 0
and d.department_id=#{department_id}
and d.department_name like '%' || #{department_name} || '%'
and e.employee_name like '%' || #{employee_name} || '%'
) deptlist where idx between 1 + #{perContentNum} * (#{startIdx}-1) and #{startIdx} * #{perContentNum} order by idx asc
